import React, { useState } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import LiquidGlass from './LiquidGlass';
import { Cpu, Menu, X, ChevronDown } from './Icons';

interface NavigationProps {
  containerRef: React.RefObject<HTMLDivElement>;
}

const Navigation: React.FC<NavigationProps> = ({ containerRef }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const location = useLocation();

  const mainPages = [
    { path: '/', label: '课程首页', icon: <Cpu className="w-4 h-4" /> },
    { path: '/objectives', label: '学习目标', icon: null },
    { path: '/careers', label: '职业发展', icon: null },
  ];

  const coursePages = [
    { path: '/course/automation-industry', label: '自动化行业认知' },
    { path: '/course/plc-basics', label: 'PLC核心定位' },
    { path: '/course/io-wiring', label: '电气接线规范' },
    { path: '/course/ladder-diagram', label: '梯形图编程' },
    { path: '/course/tia-portal', label: 'TIA Portal操作' },
    { path: '/course/safety', label: '安全强化模块' },
  ];

  const additionalPages = [
    { path: '/summary', label: '课程总结' },
    { path: '/test', label: '课堂测试' },
  ];

  const isActivePage = (path: string) => {
    if (path === '/' && location.pathname === '/') return true;
    if (path !== '/' && location.pathname.startsWith(path)) return true;
    return false;
  };

  return (
    <nav className="relative z-50 p-4 md:p-6">
      <LiquidGlass
        mouseContainer={containerRef}
        displacementScale={50}
        blurAmount={0.08}
        saturation={120}
        cornerRadius={20}
        className="backdrop-blur-md"
      >
        <div className="px-4 md:px-8 py-4">
          {/* Logo和标题 */}
          <div className="flex justify-between items-center mb-4">
            <NavLink to="/" className="flex items-center space-x-3 group">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-400 to-purple-600 rounded-lg flex items-center justify-center group-hover:scale-110 transition-transform">
                <Cpu className="w-6 h-6 text-white" />
              </div>
              <h1 className="text-xl md:text-2xl font-bold text-white">PLC控制器基础</h1>
            </NavLink>

            {/* 移动端菜单按钮 */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden"
            >
              <LiquidGlass
                displacementScale={30}
                blurAmount={0.1}
                saturation={130}
                cornerRadius={12}
                className="p-3"
              >
                {isMenuOpen ? <X className="w-6 h-6 text-white" /> : <Menu className="w-6 h-6 text-white" />}
              </LiquidGlass>
            </button>
          </div>

          {/* 导航菜单 */}
          <div className={`${isMenuOpen ? 'block' : 'hidden'} md:block`}>
            <div className="flex flex-col md:flex-row md:items-center space-y-2 md:space-y-0 md:space-x-6">
              {/* 主要页面 */}
              {mainPages.map((page) => (
                <NavLink
                  key={page.path}
                  to={page.path}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all duration-200 ${
                    isActivePage(page.path)
                      ? 'bg-white/20 text-white'
                      : 'text-white/80 hover:text-white hover:bg-white/10'
                  }`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {page.icon}
                  <span className="font-medium">{page.label}</span>
                </NavLink>
              ))}

              {/* 课程章节下拉菜单 */}
              <div className="relative">
                <button
                  onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all duration-200 ${
                    location.pathname.startsWith('/course')
                      ? 'bg-white/20 text-white'
                      : 'text-white/80 hover:text-white hover:bg-white/10'
                  }`}
                >
                  <span className="font-medium">课程章节</span>
                  <ChevronDown className={`w-4 h-4 transition-transform ${isDropdownOpen ? 'rotate-180' : ''}`} />
                </button>

                {/* 下拉菜单内容 */}
                {isDropdownOpen && (
                  <div className="absolute top-full left-0 mt-2 md:w-64 z-10">
                    <LiquidGlass
                      displacementScale={40}
                      blurAmount={0.1}
                      saturation={130}
                      cornerRadius={12}
                      className="p-2"
                    >
                      <div className="space-y-1">
                        {coursePages.map((page, index) => (
                          <NavLink
                            key={page.path}
                            to={page.path}
                            className={`block px-4 py-3 rounded-lg transition-all duration-200 ${
                              isActivePage(page.path)
                                ? 'bg-white/20 text-white'
                                : 'text-white/80 hover:text-white hover:bg-white/10'
                            }`}
                            onClick={() => {
                              setIsDropdownOpen(false);
                              setIsMenuOpen(false);
                            }}
                          >
                            <div className="flex items-center space-x-3">
                              <span className="w-6 h-6 bg-gradient-to-r from-blue-400 to-purple-600 rounded text-white text-sm flex items-center justify-center font-bold">
                                {index + 1}
                              </span>
                              <span className="font-medium">{page.label}</span>
                            </div>
                          </NavLink>
                        ))}
                      </div>
                    </LiquidGlass>
                  </div>
                )}
              </div>

              {/* 其他页面 */}
              {additionalPages.map((page) => (
                <NavLink
                  key={page.path}
                  to={page.path}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all duration-200 ${
                    isActivePage(page.path)
                      ? 'bg-white/20 text-white'
                      : 'text-white/80 hover:text-white hover:bg-white/10'
                  }`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  <span className="font-medium">{page.label}</span>
                </NavLink>
              ))}
            </div>
          </div>
        </div>
      </LiquidGlass>
    </nav>
  );
};

export default Navigation;
