import React from 'react';
import { Link } from 'react-router-dom';
import LiquidGlass from '../components/LiquidGlass';
import { 
  Award, 
  TrendingUp, 
  Users, 
  Cpu,
  Settings,
  Zap,
  Target,
  CheckCircle,
  ArrowRight
} from '../components/Icons';

interface CareersPageProps {
  containerRef: React.RefObject<HTMLDivElement>;
}

const CareersPage: React.FC<CareersPageProps> = ({ containerRef }) => {
  const careerPaths = [
    {
      title: "PLC编程工程师",
      demand: "需求量大",
      threshold: "门槛中等",
      salaryRange: "8K-20K",
      skills: "梯形图编程和硬件配置",
      description: "负责工业自动化系统的程序设计、调试与维护，是自动化领域的核心技术岗位。",
      icon: <Cpu className="w-8 h-8" />,
      color: "from-blue-400 to-purple-600",
      responsibilities: [
        "编写和调试PLC控制程序",
        "设计自动化控制系统方案",
        "参与工厂自动化项目实施",
        "维护和优化现有控制系统"
      ],
      requirements: [
        "熟练掌握梯形图编程",
        "了解工业网络通信协议",
        "具备电气图识读能力",
        "熟悉TIA Portal等编程软件"
      ]
    },
    {
      title: "自动化技术员",
      demand: "需求稳定",
      threshold: "入门门槛较低",
      salaryRange: "5K-12K",
      skills: "基础操作和维护",
      description: "从事自动化设备的安装、调试、维护工作，是制造业不可缺少的技术支撑岗位。",
      icon: <Settings className="w-8 h-8" />,
      color: "from-green-400 to-blue-600",
      responsibilities: [
        "自动化设备的日常维护",
        "协助工程师进行设备调试",
        "故障排查和简单维修",
        "设备操作培训和技术支持"
      ],
      requirements: [
        "掌握基本电气知识",
        "了解PLC工作原理",
        "具备动手操作能力",
        "学会使用常用仪表工具"
      ]
    },
    {
      title: "工控设备调试员",
      demand: "专业性强",
      threshold: "技术要求高",
      salaryRange: "10K-25K",
      skills: "电气图识读和故障诊断",
      description: "专门负责复杂工控设备的现场调试、故障诊断与技术支持，要求较高的专业技能。",
      icon: <Zap className="w-8 h-8" />,
      color: "from-orange-400 to-red-600",
      responsibilities: [
        "复杂设备的现场调试",
        "故障诊断和技术分析",
        "制定调试和维护方案",
        "技术文档编写和培训"
      ],
      requirements: [
        "深入理解控制系统原理",
        "熟练的故障诊断技能",
        "丰富的现场调试经验",
        "良好的沟通和问题解决能力"
      ]
    }
  ];

  const industryApplications = [
    {
      title: "离散制造业",
      examples: "汽车装配线、电子产品生产线",
      characteristics: "高精度、高速度、灵活性要求",
      plcRole: "控制机械手、传送带、焊接设备等"
    },
    {
      title: "过程控制业",
      examples: "化工厂反应罐、石油精炼、水处理",
      characteristics: "连续过程、安全性要求极高",
      plcRole: "温度压力控制、流量调节、安全联锁"
    },
    {
      title: "基础设施",
      examples: "智能楼宇、交通信号、水电站",
      characteristics: "长期稳定运行、远程监控",
      plcRole: "环境控制、设备管理、数据采集"
    }
  ];

  const skillDevelopment = [
    {
      level: "入门阶段",
      duration: "1-3个月",
      skills: ["基础电气知识", "PLC工作原理", "简单梯形图编程"],
      certifications: ["电工证", "PLC操作员证书"],
      focus: "理论基础和基本操作"
    },
    {
      level: "进阶阶段",
      duration: "6-12个月",
      skills: ["复杂程序设计", "HMI界面开发", "网络通信配置"],
      certifications: ["PLC编程工程师证书", "自动化系统集成师"],
      focus: "实际项目经验积累"
    },
    {
      level: "专家阶段",
      duration: "2-3年",
      skills: ["系统架构设计", "故障诊断专家", "团队管理"],
      certifications: ["高级工程师", "项目管理师"],
      focus: "技术领导和创新能力"
    }
  ];

  return (
    <main className="relative z-10 px-6 pb-12">
      {/* 页面标题 */}
      <section className="mb-12">
        <LiquidGlass
          mouseContainer={containerRef}
          displacementScale={80}
          blurAmount={0.1}
          saturation={140}
          elasticity={0.25}
          cornerRadius={24}
          className="text-center p-8"
        >
          <div className="flex items-center justify-center mb-4">
            <Award className="w-8 h-8 text-green-400 mr-3" />
            <h1 className="text-3xl md:text-4xl font-bold text-white">职业发展路径</h1>
          </div>
          <p className="text-lg text-white/80 max-w-3xl mx-auto leading-relaxed">
            探索PLC自动化领域的职业机会，规划您的技术成长之路，开启工业4.0时代的精彩职业生涯。
          </p>
        </LiquidGlass>
      </section>

      {/* 核心职业岗位 */}
      <section className="mb-16">
        <LiquidGlass
          mouseContainer={containerRef}
          displacementScale={60}
          blurAmount={0.08}
          saturation={130}
          cornerRadius={20}
          className="mb-8"
        >
          <div className="p-8">
            <h2 className="text-2xl font-bold text-white mb-4 flex items-center">
              <TrendingUp className="w-6 h-6 mr-3 text-blue-400" />
              核心职业岗位
            </h2>
            <p className="text-white/80">
              PLC技术是工业自动化的核心，相关岗位需求旺盛，职业发展前景广阔。
            </p>
          </div>
        </LiquidGlass>

        <div className="space-y-8">
          {careerPaths.map((career, index) => (
            <LiquidGlass
              key={index}
              mouseContainer={containerRef}
              displacementScale={50}
              blurAmount={0.08}
              saturation={130}
              cornerRadius={16}
              className="p-8"
            >
              <div className="flex flex-col lg:flex-row gap-8">
                {/* 左侧：基本信息 */}
                <div className="lg:w-1/3 flex-shrink-0">
                  <div className="flex items-start space-x-4 mb-4">
                    <div className={`w-16 h-16 bg-gradient-to-r ${career.color} rounded-xl flex items-center justify-center flex-shrink-0`}>
                      {career.icon}
                    </div>
                    <div>
                      <h3 className="text-xl font-bold text-white mb-2">
                        {career.title}
                      </h3>
                      <p className="text-white/80 text-sm leading-relaxed">
                        {career.description}
                      </p>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <LiquidGlass
                      displacementScale={30}
                      blurAmount={0.05}
                      saturation={120}
                      cornerRadius={12}
                      className="p-3 text-center"
                    >
                      <div className="text-white/60 text-xs mb-1">市场需求</div>
                      <div className="text-green-400 font-medium">{career.demand}</div>
                    </LiquidGlass>

                    <LiquidGlass
                      displacementScale={30}
                      blurAmount={0.05}
                      saturation={120}
                      cornerRadius={12}
                      className="p-3 text-center"
                    >
                      <div className="text-white/60 text-xs mb-1">入门门槛</div>
                      <div className="text-blue-400 font-medium">{career.threshold}</div>
                    </LiquidGlass>

                    <LiquidGlass
                      displacementScale={30}
                      blurAmount={0.05}
                      saturation={120}
                      cornerRadius={12}
                      className="p-3 text-center col-span-2"
                    >
                      <div className="text-white/60 text-xs mb-1">薪资范围</div>
                      <div className="text-purple-400 font-medium">{career.salaryRange}</div>
                    </LiquidGlass>
                  </div>
                </div>

                {/* 右侧：详细信息 */}
                <div className="lg:w-2/3 flex-grow">
                  <div className="grid md:grid-cols-2 gap-6">
                    <div>
                      <h4 className="text-lg font-semibold text-white mb-3 flex items-center">
                        <CheckCircle className="w-5 h-5 mr-2 text-green-400" />
                        主要职责
                      </h4>
                      <div className="space-y-2">
                        {career.responsibilities.map((resp, respIndex) => (
                          <div key={respIndex} className="flex items-start space-x-2">
                            <div className="w-1.5 h-1.5 bg-green-400 rounded-full mt-2 flex-shrink-0"></div>
                            <span className="text-white/80 text-sm">{resp}</span>
                          </div>
                        ))}
                      </div>
                    </div>

                    <div>
                      <h4 className="text-lg font-semibold text-white mb-3 flex items-center">
                        <Target className="w-5 h-5 mr-2 text-blue-400" />
                        技能要求
                      </h4>
                      <div className="space-y-2">
                        {career.requirements.map((req, reqIndex) => (
                          <div key={reqIndex} className="flex items-start space-x-2">
                            <div className="w-1.5 h-1.5 bg-blue-400 rounded-full mt-2 flex-shrink-0"></div>
                            <span className="text-white/80 text-sm">{req}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </LiquidGlass>
          ))}
        </div>
      </section>

      {/* 行业应用领域 */}
      <section className="mb-16">
