import React from 'react';
import { Link } from 'react-router-dom';
import LiquidGlass from '../components/LiquidGlass';
import { 
  BookOpen, 
  Settings, 
  Zap, 
  Cpu, 
  ChevronRight,
  PlayCircle,
  Clock,
  Users,
  Target,
  Award,
  CheckCircle,
  ArrowRight
} from '../components/Icons';

const HomePage: React.FC = () => {
  const courseModules = [
    {
      id: 1,
      title: "自动化行业基本构成",
      description: "传感层、控制层(PLC)、执行层、管理层全景认知",
      icon: <Settings className="w-6 h-6" />,
      color: "from-blue-400 to-purple-600",
      link: "/course/automation-industry"
    },
    {
      id: 2,
      title: "PLC本质与特性",
      description: "工业控制器本质、扫描周期、实时性与硬件基础",
      icon: <Cpu className="w-6 h-6" />,
      color: "from-purple-400 to-pink-600",
      link: "/course/plc-basics"
    },
    {
      id: 3,
      title: "I/O接线规范",
      description: "两线制、三线制传感器接线与安全规范",
      icon: <Zap className="w-6 h-6" />,
      color: "from-green-400 to-blue-600",
      link: "/course/io-wiring"
    },
    {
      id: 4,
      title: "梯形图五大基础指令",
      description: "常开、常闭触点、输出线圈、定时器、计数器",
      icon: <BookOpen className="w-6 h-6" />,
      color: "from-orange-400 to-red-600",
      link: "/course/ladder-diagram"
    },
    {
      id: 5,
      title: "TIA Portal基础操作",
      description: "工程创建、梯形图编写、程序下载监控",
      icon: <BookOpen className="w-6 h-6" />,
      color: "from-teal-400 to-blue-600",
      link: "/course/tia-portal"
    },
    {
      id: 6,
      title: "急停的梯形图禁忌",
      description: "安全设计优先原则、双线圈冲突避免",
      icon: <Target className="w-6 h-6" />,
      color: "from-red-400 to-pink-600",
      link: "/course/safety"
    }
  ];

  const quickLinks = [
    {
      title: "学习目标",
      description: "了解课程的学习目标和能力要求",
      link: "/objectives",
      icon: <Target className="w-5 h-5" />
    },
    {
      title: "职业发展",
      description: "探索PLC相关的职业发展路径",
      link: "/careers",
      icon: <Award className="w-5 h-5" />
    },
    {
      title: "课程总结",
      description: "回顾课程要点和知识体系",
      link: "/summary",
      icon: <BookOpen className="w-5 h-5" />
    },
    {
      title: "课堂测试",
      description: "检验学习成果，巩固知识",
      link: "/test",
      icon: <CheckCircle className="w-5 h-5" />
    }
  ];

  const stats = [
    { label: '学习模块', value: '6个', icon: <BookOpen className="w-6 h-6" /> },
    { label: '学习时长', value: '60分钟', icon: <Clock className="w-6 h-6" /> },
    { label: '技能掌握', value: '15项', icon: <Target className="w-6 h-6" /> },
    { label: '就业岗位', value: '3类', icon: <Users className="w-6 h-6" /> }
  ];

  const modules = [
    {
      title: '自动化行业认知',
      description: '理解自动化系统四层架构，建立行业认知基础',
      icon: <Settings className="w-8 h-8" />,
      path: '/course/automation-industry',
      color: 'from-blue-400 to-cyan-600',
      difficulty: '基础',
      duration: '10分钟'
    },
    {
      title: 'PLC核心定位', 
      description: '掌握PLC工作原理、扫描周期和选型方法',
      icon: <Cpu className="w-8 h-8" />,
      path: '/course/plc-basics',
      color: 'from-green-400 to-blue-600',
      difficulty: '重点',
      duration: '15分钟'
    },
    {
      title: '电气接线规范',
      description: '学习24V安全电压接线和安全电路设计',
      icon: <Zap className="w-8 h-8" />,
      path: '/course/io-wiring',
      color: 'from-yellow-400 to-orange-600',
      difficulty: '实操',
      duration: '10分钟'
    },
    {
      title: '梯形图编程',
      description: '掌握五大基础指令，编写控制逻辑程序',
      icon: <BookOpen className="w-8 h-8" />,
      path: '/course/ladder-diagram',
      color: 'from-purple-400 to-pink-600',
      difficulty: '核心',
      duration: '15分钟'
    },
    {
      title: 'TIA Portal操作',
      description: '熟练使用西门子编程软件进行项目开发',
      icon: <Target className="w-8 h-8" />,
      path: '/course/tia-portal',
      color: 'from-indigo-400 to-purple-600',
      difficulty: '实操',
      duration: '15分钟'
    },
    {
      title: '安全强化模块',
      description: '强化工业安全意识，掌握安全设计原则',
      icon: <Award className="w-8 h-8" />,
      path: '/course/safety',
      color: 'from-red-400 to-pink-600',
      difficulty: '重要',
      duration: '5分钟'
    }
  ];

  return (
    <main className="relative z-10 px-6 pb-12">
      {/* 课程介绍头部 */}
      <section className="mb-16 text-center">
        <LiquidGlass
          displacementScale={80}
          blurAmount={0.1}
          saturation={140}
          elasticity={0.25}
          cornerRadius={24}
          className="inline-block mb-8"
        >
          <div className="px-12 py-8">
            <h2 className="text-4xl md:text-5xl font-bold text-white mb-4">
              工业自动化核心技术
            </h2>
            <p className="text-lg md:text-xl text-white/80 max-w-2xl mx-auto leading-relaxed">
              掌握PLC控制器基础知识，开启自动化工程师职业之路
            </p>
            <div className="mt-6 flex flex-wrap justify-center gap-4">
              <LiquidGlass
                displacementScale={40}
                blurAmount={0.05}
                saturation={150}
                cornerRadius={50}
                className="inline-block"
              >
                <Link to="/course/automation-industry" className="px-8 py-3 text-white font-medium flex items-center space-x-2 hover:scale-105 transition-transform">
                  <PlayCircle className="w-5 h-5" />
                  <span>开始学习</span>
                </Link>
              </LiquidGlass>
              <LiquidGlass
                displacementScale={40}
                blurAmount={0.05}
                saturation={150}
                cornerRadius={50}
                className="inline-block"
              >
                <Link to="/objectives" className="px-8 py-3 text-white font-medium flex items-center space-x-2 hover:scale-105 transition-transform">
                  <Target className="w-5 h-5" />
                  <span>学习目标</span>
                </Link>
              </LiquidGlass>
            </div>
          </div>
        </LiquidGlass>
      </section>

      {/* 课程统计信息 */}
      <section className="mb-16">
        <div className="grid md:grid-cols-3 gap-6">
          {stats.map((stat, index) => (
            <LiquidGlass
              displacementScale={50}
              blurAmount={0.08}
              saturation={130}
              cornerRadius={16}
              className="p-6 text-center"
            >
              <div className="w-12 h-12 bg-gradient-to-r from-blue-400 to-purple-600 rounded-lg flex items-center justify-center mx-auto mb-4">
                {stat.icon}
              </div>
              <h4 className="text-lg font-semibold text-white mb-2">{stat.value}</h4>
              <p className="text-white/70 text-sm">{stat.label}</p>
            </LiquidGlass>
          ))}
        </div>
      </section>

      {/* 课程模块 */}
      <section className="mb-16">
        <LiquidGlass
          displacementScale={60}
          blurAmount={0.08}
          saturation={130}
          cornerRadius={20}
          className="mb-8"
        >
          <div className="p-8">
            <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
              <BookOpen className="w-6 h-6 mr-3 text-purple-400" />
              课程内容模块
            </h3>
            <p className="text-white/80 mb-4">
              本课程共包含6个核心模块，从行业认知到实际操作，循序渐进地建立完整的PLC知识体系。
            </p>
          </div>
        </LiquidGlass>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {modules.map((module, index) => (
            <Link key={module.id} to={module.path}>
              <LiquidGlass
                displacementScale={50}
                blurAmount={0.1}
                saturation={140}
                elasticity={0.2}
                cornerRadius={16}
                className="group hover:scale-105 transition-all duration-300 cursor-pointer h-full"
              >
                <div className="p-6 h-full flex flex-col">
                  <div className={`w-12 h-12 bg-gradient-to-r ${module.color} rounded-lg flex items-center justify-center mb-4 group-hover:scale-110 transition-transform`}>
                    {module.icon}
                  </div>
                  <h4 className="text-lg font-semibold text-white mb-2">
                    {module.title}
                  </h4>
                  <p className="text-white/70 text-sm leading-relaxed mb-4 flex-grow">
                    {module.description}
                  </p>
                  <div className="flex items-center text-blue-400 text-sm font-medium">
                    <span>了解更多</span>
                    <ChevronRight className="w-4 h-4 ml-1 group-hover:translate-x-1 transition-transform" />
                  </div>
                </div>
              </LiquidGlass>
            </Link>
          ))}
        </div>
      </section>

      {/* 学习路径 */}
      <section className="mb-16">
        <LiquidGlass
          displacementScale={60}
          blurAmount={0.08}
          saturation={130}
          cornerRadius={20}
          className="mb-8"
        >
          <div className="p-8">
            <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
              <BookOpen className="w-6 h-6 mr-3 text-purple-400" />
              学习路径推荐
            </h3>
          </div>
        </LiquidGlass>

        <div className="grid md:grid-cols-3 gap-8">
          <div className="text-center">
            <div className="w-16 h-16 bg-gradient-to-r from-green-400 to-blue-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <span className="text-white text-xl font-bold">1</span>
            </div>
            <h3 className="text-lg font-semibold text-white mb-2">理论基础</h3>
            <p className="text-white/70 text-sm">
              学习自动化行业认知和PLC核心定位，建立扎实的理论基础
            </p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-gradient-to-r from-blue-400 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <span className="text-white text-xl font-bold">2</span>
            </div>
            <h3 className="text-lg font-semibold text-white mb-2">实践技能</h3>
            <p className="text-white/70 text-sm">
              掌握电气接线和梯形图编程，培养实际操作能力
            </p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-gradient-to-r from-purple-400 to-pink-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <span className="text-white text-xl font-bold">3</span>
            </div>
            <h3 className="text-lg font-semibold text-white mb-3">综合应用</h3>
            <p className="text-white/70 text-sm">
              学习TIA Portal操作和安全规范，形成完整的应用能力
            </p>
          </div>
        </div>
      </section>

      {/* 快速导航 */}
      <section>
        <LiquidGlass
          displacementScale={60}
          blurAmount={0.08}
          saturation={130}
          cornerRadius={20}
          className="mb-8"
        >
          <div className="p-8">
            <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
              <Target className="w-6 h-6 mr-3 text-green-400" />
              快速导航
            </h3>
          </div>
        </LiquidGlass>

        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
          {quickLinks.map((item, index) => (
            <Link key={index} to={item.link}>
              <LiquidGlass
                displacementScale={40}
                blurAmount={0.08}
                saturation={130}
                cornerRadius={16}
                className="p-6 text-center group hover:scale-105 transition-all duration-300 cursor-pointer"
              >
                <div className="flex flex-col items-center">
                  <div className="w-10 h-10 bg-gradient-to-r from-blue-400 to-purple-600 rounded-lg flex items-center justify-center mb-3 group-hover:scale-110 transition-transform">
                    {item.icon}
                  </div>
                  <h4 className="text-lg font-semibold text-white mb-2">
                    {item.title}
                  </h4>
                  <p className="text-white/70 text-sm leading-relaxed">
                    {item.description}
                  </p>
                </div>
              </LiquidGlass>
            </Link>
          ))}
        </div>
      </section>
    </main>
  );
};

export default HomePage;
