import React, { useRef } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navigation from './components/Navigation';
import HomePage from './pages/HomePage';
import ObjectivesPage from './pages/ObjectivesPage';
import CareersPage from './pages/CareersPage';
import AutomationIndustryPage from './pages/course/AutomationIndustryPage';
import PLCBasicsPage from './pages/course/PLCBasicsPage';
import IOWiringPage from './pages/course/IOWiringPage';
import LadderDiagramPage from './pages/course/LadderDiagramPage';

const App: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);

  return (
    <Router>
      <div ref={containerRef} className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 relative overflow-hidden">
        {/* 背景装饰 */}
        <div className="absolute inset-0 opacity-30">
          <div className="absolute top-20 left-20 w-72 h-72 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow"></div>
          <div className="absolute top-40 right-20 w-72 h-72 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow delay-1000"></div>
          <div className="absolute bottom-20 left-40 w-72 h-72 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow delay-2000"></div>
        </div>

        {/* 导航栏 */}
        <Navigation containerRef={containerRef} />

        {/* 路由内容 */}
        <Routes>
          <Route path="/" element={<HomePage containerRef={containerRef} />} />
          <Route path="/objectives" element={<ObjectivesPage containerRef={containerRef} />} />
          <Route path="/careers" element={<CareersPage containerRef={containerRef} />} />
          <Route path="/summary" element={<div className="p-6 text-white text-center">课程总结页面（开发中）</div>} />
          <Route path="/test" element={<div className="p-6 text-white text-center">课堂测试页面（开发中）</div>} />

          {/* 课程章节路由 */}
          <Route path="/course/automation-industry" element={<AutomationIndustryPage />} />
          <Route path="/course/plc-basics" element={<PLCBasicsPage />} />
          <Route path="/course/io-wiring" element={<IOWiringPage />} />
          <Route path="/course/ladder-diagram" element={<div className="p-6 text-white text-center">梯形图编程页面（开发中）</div>} />
          <Route path="/course/tia-portal" element={<div className="p-6 text-white text-center">TIA Portal操作页面（开发中）</div>} />
          <Route path="/course/safety" element={<div className="p-6 text-white text-center">安全强化模块页面（开发中）</div>} />
        </Routes>

        {/* 页脚 */}
        <footer className="relative z-10 p-6 mt-16">
          <div className="backdrop-blur-md bg-white/10 border border-white/20 rounded-2xl p-6 text-center">
            <p className="text-white/60 text-sm">
              © 2025 PLC控制器基础教学网页 | 现代化玻璃风格界面设计
            </p>
          </div>
        </footer>
      </div>
    </Router>
  );
};

export default App;
