import React, { useRef, useState } from 'react';
import LiquidGlass from './components/LiquidGlass';
import { 
  BookOpen, 
  Settings, 
  Zap, 
  Cpu, 
  ChevronRight,
  PlayCircle,
  Clock,
  Users,
  Target,
  Award,
  Menu,
  X
} from './components/Icons';

const App: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const courseModules = [
    {
      id: 1,
      title: "自动化行业基本构成",
      description: "传感层、控制层(PLC)、执行层、管理层全景认知",
      icon: <Settings className="w-6 h-6" />,
      color: "from-blue-400 to-purple-600"
    },
    {
      id: 2,
      title: "PLC本质与特性",
      description: "工业控制器本质、扫描周期、实时性与硬件基础",
      icon: <Cpu className="w-6 h-6" />,
      color: "from-purple-400 to-pink-600"
    },
    {
      id: 3,
      title: "I/O接线规范",
      description: "两线制、三线制传感器接线与安全规范",
      icon: <Zap className="w-6 h-6" />,
      color: "from-green-400 to-blue-600"
    },
    {
      id: 4,
      title: "梯形图五大基础指令",
      description: "常开、常闭触点、输出线圈、定时器、计数器",
      icon: <BookOpen className="w-6 h-6" />,
      color: "from-orange-400 to-red-600"
    }
  ];

  const learningObjectives = [
    "掌握电气图识读技能与生产流程认知",
    "熟练使用梯形图编程工具进行基础操作",
    "具备PLC硬件I/O模块接线能力",
    "理解PLC循环扫描工作原理",
    "遵循安全设计优先原则"
  ];

  const careerPaths = [
    {
      title: "PLC编程工程师",
      demand: "需求量大",
      threshold: "门槛中等",
      skills: "梯形图编程和硬件配置"
    },
    {
      title: "自动化技术员",
      demand: "需求稳定",
      threshold: "入门门槛较低",
      skills: "基础操作和维护"
    },
    {
      title: "工控设备调试员",
      demand: "专业性强",
      threshold: "技术要求高",
      skills: "电气图识读和故障诊断"
    }
  ];

  return (
    <div ref={containerRef} className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 relative overflow-hidden">
      {/* 背景装饰 */}
      <div className="absolute inset-0 opacity-30">
        <div className="absolute top-20 left-20 w-72 h-72 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow"></div>
        <div className="absolute top-40 right-20 w-72 h-72 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow delay-1000"></div>
        <div className="absolute bottom-20 left-40 w-72 h-72 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl animate-pulse-slow delay-2000"></div>
      </div>

      {/* 移动端菜单按钮 */}
      <div className="md:hidden fixed top-4 right-4 z-50">
        <LiquidGlass
          displacementScale={30}
          blurAmount={0.1}
          saturation={130}
          cornerRadius={12}
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className="p-3"
        >
          {isMenuOpen ? <X className="w-6 h-6 text-white" /> : <Menu className="w-6 h-6 text-white" />}
        </LiquidGlass>
      </div>

      {/* 导航栏 */}
      <nav className="relative z-40 p-6">
        <LiquidGlass
          mouseContainer={containerRef}
          displacementScale={50}
          blurAmount={0.08}
          saturation={120}
          cornerRadius={20}
          className="backdrop-blur-md"
        >
          <div className="px-8 py-4 flex justify-between items-center">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-400 to-purple-600 rounded-lg flex items-center justify-center">
                <Cpu className="w-6 h-6 text-white" />
              </div>
              <h1 className="text-2xl font-bold text-white">PLC控制器基础</h1>
            </div>
            <div className="hidden md:flex items-center space-x-6 text-white/80">
              <span className="flex items-center space-x-2">
                <Clock className="w-4 h-4" />
                <span>1小时课程</span>
              </span>
              <span className="flex items-center space-x-2">
                <Users className="w-4 h-4" />
                <span>大专生</span>
              </span>
            </div>
          </div>
        </LiquidGlass>
      </nav>

      {/* 主要内容区域 */}
      <main className="relative z-10 px-6 pb-12">
        {/* 课程介绍头部 */}
        <section className="mb-16 text-center">
          <LiquidGlass
            mouseContainer={containerRef}
            displacementScale={80}
            blurAmount={0.1}
            saturation={140}
            elasticity={0.25}
            cornerRadius={24}
            className="inline-block mb-8"
          >
            <div className="px-12 py-8">
              <h2 className="text-5xl font-bold text-white mb-4">
                工业自动化核心技术
              </h2>
              <p className="text-xl text-white/80 max-w-2xl mx-auto leading-relaxed">
                掌握PLC控制器基础知识，开启自动化工程师职业之路
              </p>
              <div className="mt-6">
                <LiquidGlass
                  displacementScale={40}
                  blurAmount={0.05}
                  saturation={150}
                  cornerRadius={50}
                  className="inline-block"
                >
                  <button className="px-8 py-3 text-white font-medium flex items-center space-x-2 hover:scale-105 transition-transform">
                    <PlayCircle className="w-5 h-5" />
                    <span>开始学习</span>
                  </button>
                </LiquidGlass>
              </div>
            </div>
          </LiquidGlass>
        </section>

        {/* 学习目标 */}
        <section className="mb-16">
          <LiquidGlass
            mouseContainer={containerRef}
            displacementScale={60}
            blurAmount={0.08}
            saturation={130}
            cornerRadius={20}
            className="mb-8"
          >
            <div className="p-8">
              <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                <Target className="w-6 h-6 mr-3 text-blue-400" />
                学习目标
              </h3>
              <div className="grid md:grid-cols-2 gap-4">
                {learningObjectives.map((objective, index) => (
                  <LiquidGlass
                    key={index}
                    displacementScale={30}
                    blurAmount={0.05}
                    saturation={120}
                    cornerRadius={12}
                    className="p-4"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-2 h-2 bg-blue-400 rounded-full"></div>
                      <span className="text-white/90">{objective}</span>
                    </div>
                  </LiquidGlass>
                ))}
              </div>
            </div>
          </LiquidGlass>
        </section>

        {/* 课程模块 */}
        <section className="mb-16">
          <LiquidGlass
            mouseContainer={containerRef}
            displacementScale={60}
            blurAmount={0.08}
            saturation={130}
            cornerRadius={20}
            className="mb-8"
          >
            <div className="p-8">
              <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                <BookOpen className="w-6 h-6 mr-3 text-purple-400" />
                课程内容
              </h3>
            </div>
          </LiquidGlass>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
            {courseModules.map((module, index) => (
              <LiquidGlass
                key={module.id}
                mouseContainer={containerRef}
                displacementScale={50}
                blurAmount={0.1}
                saturation={140}
                elasticity={0.2}
                cornerRadius={16}
                className="group hover:scale-105 transition-all duration-300 cursor-pointer"
              >
                <div className="p-6 h-full">
                  <div className={`w-12 h-12 bg-gradient-to-r ${module.color} rounded-lg flex items-center justify-center mb-4 group-hover:scale-110 transition-transform`}>
                    {module.icon}
                  </div>
                  <h4 className="text-lg font-semibold text-white mb-2">
                    {module.title}
                  </h4>
                  <p className="text-white/70 text-sm leading-relaxed mb-4">
                    {module.description}
                  </p>
                  <div className="flex items-center text-blue-400 text-sm font-medium">
                    <span>了解更多</span>
                    <ChevronRight className="w-4 h-4 ml-1 group-hover:translate-x-1 transition-transform" />
                  </div>
                </div>
              </LiquidGlass>
            ))}
          </div>
        </section>

        {/* 职业发展路径 */}
        <section className="mb-16">
          <LiquidGlass
            mouseContainer={containerRef}
            displacementScale={60}
            blurAmount={0.08}
            saturation={130}
            cornerRadius={20}
          >
            <div className="p-8">
              <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                <Award className="w-6 h-6 mr-3 text-green-400" />
                职业发展路径
              </h3>
              <div className="grid md:grid-cols-3 gap-6">
                {careerPaths.map((career, index) => (
                  <LiquidGlass
                    key={index}
                    displacementScale={40}
                    blurAmount={0.06}
                    saturation={125}
                    cornerRadius={14}
                    className="p-6"
                  >
                    <div>
                      <h4 className="text-lg font-semibold text-white mb-3">
                        {career.title}
                      </h4>
                      <div className="space-y-2 text-white/80 text-sm">
                        <div className="flex justify-between">
                          <span>市场需求:</span>
                          <span className="text-green-400">{career.demand}</span>
                        </div>
                        <div className="flex justify-between">
                          <span>入门门槛:</span>
                          <span className="text-blue-400">{career.threshold}</span>
                        </div>
                        <div className="mt-3">
                          <span className="text-white/60">核心技能:</span>
                          <p className="text-white/80 mt-1">{career.skills}</p>
                        </div>
                      </div>
                    </div>
                  </LiquidGlass>
                ))}
              </div>
            </div>
          </LiquidGlass>
        </section>

        {/* 课程信息卡片 */}
        <section>
          <div className="grid md:grid-cols-3 gap-6">
            <LiquidGlass
              mouseContainer={containerRef}
              displacementScale={50}
              blurAmount={0.08}
              saturation={130}
              cornerRadius={16}
              className="p-6 text-center"
            >
              <Clock className="w-8 h-8 text-blue-400 mx-auto mb-3" />
              <h4 className="text-lg font-semibold text-white mb-2">课程时长</h4>
              <p className="text-white/80">1小时精品课程</p>
              <p className="text-white/60 text-sm mt-1">可根据实际调整</p>
            </LiquidGlass>

            <LiquidGlass
              mouseContainer={containerRef}
              displacementScale={50}
              blurAmount={0.08}
              saturation={130}
              cornerRadius={16}
              className="p-6 text-center"
            >
              <Users className="w-8 h-8 text-purple-400 mx-auto mb-3" />
              <h4 className="text-lg font-semibold text-white mb-2">目标受众</h4>
              <p className="text-white/80">大专院校学生</p>
              <p className="text-white/60 text-sm mt-1">工程技术专业</p>
            </LiquidGlass>

            <LiquidGlass
              mouseContainer={containerRef}
              displacementScale={50}
              blurAmount={0.08}
              saturation={130}
              cornerRadius={16}
              className="p-6 text-center"
            >
              <Award className="w-8 h-8 text-green-400 mx-auto mb-3" />
              <h4 className="text-lg font-semibold text-white mb-2">课程特色</h4>
              <p className="text-white/80">理论与实践结合</p>
              <p className="text-white/60 text-sm mt-1">约13814字深度内容</p>
            </LiquidGlass>
          </div>
        </section>
      </main>

      {/* 页脚 */}
      <footer className="relative z-10 p-6 mt-16">
        <LiquidGlass
          mouseContainer={containerRef}
          displacementScale={40}
          blurAmount={0.06}
          saturation={120}
          cornerRadius={16}
          className="text-center p-6"
        >
          <p className="text-white/60 text-sm">
            © 2025 PLC控制器基础教学网页 | 现代化玻璃风格界面设计
          </p>
        </LiquidGlass>
      </footer>
    </div>
  );
};

export default App;
